mapper.update:
update user
set username = #{username,jdbcType=VARCHAR},
  `NAME` = #{name,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  balance = #{balance,jdbcType=DECIMAL}
where Id = #{id,jdbcType=INTEGER}

mapper.select:
select Id, username, `NAME`, age, balance
from user
where Id = #{id,jdbcType=INTEGER}
